import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import CommonButton from "../Common/CommonButton";

const Header = () => {
	const [isAuthenticated, setIsAuthenticated] = useState(false);
	useEffect(() => {
		const authToken = sessionStorage.getItem("token");
		if (authToken) setIsAuthenticated(true);
	}, []);
	return (
		<nav className="navbar bg-secondary navbar-dark p-4">
			<div className="container">
				<span className="navbar-brand mb-0 h1">
					<Link to="/" className="navbar-brand mb-0 h1">
						CheckList-Champ
					</Link>
				</span>
				<ul className="navbar-nav ms-auto flex-row">
					{isAuthenticated && (
						<li className="nav-item me-4">
							{/* <Link className="btn btn-primary" to="/note-detail">
							View Your Notes
						</Link> */}
							<CommonButton text="View Your Notes" />
						</li>
					)}
					{isAuthenticated && (
						<li className="nav-item">
							{/* <button className="btn btn-primary">Logout User</button> */}
							<CommonButton text="Logout" />
						</li>
					)}
				</ul>
			</div>
		</nav>
	);
};

export default Header;
